import React from "react";

export default function AboutUs(){
    return (
        <div id="about" className="about-container">
            <div className="about-left">
                <h1>About Us</h1>
                <p>Welcome to Jang's International Restaurant, where authentic Korean flavors meet global inspiration! At Jang's, we pride ourselves on serving traditional Korean dishes crafted with the freshest ingredients and time-honored recipes. From our sizzling bulgogi to our savory bibimbap, each dish reflects the rich culinary heritage of Korea, while our fusion options introduce an exciting blend of international tastes. Whether you're looking for a classic Korean meal or a modern twist, Jang's offers a warm, inviting atmosphere perfect for family gatherings, casual dining, or a night out with friends. Come experience the heart of Korea, right here at Jang's!</p>
            </div>
            <div className="about-right">
                <img src="images/restaurant_image.jpeg"></img>
            </div>
        </div>
    );
}
